<?php  class mainController extends Controller {        public function __construct() {      parent::__construct();      $this->_main = $this->loadModel('main');                     }        public function index() {          }    function gets($tabla,$item,$valor,$modelo=false){        switch ($item) {          case 'familias':            $item='familia';            break;          case 'familia_id':            $item='familia';          break;           case 'mercancia_id':            $item='nombre';          break;          default:            # code...            break;        }        $data = $this->_main->Gets($tabla,$item,$valor,$modelo);         echo json_encode($data);             }    function tienda($valor){      //echo $valor;      Session::destroy('monedatienda');      $query = "SELECT unidad_negocio.id, unidad_negocio.codigo, unidad_negocio.rif, unidad_negocio.razon_social, unidad_negocio.nombre, unidad_negocio.numero_autorizacion, unidad_negocio.modelo_has_submodelo_id,  unidad_negocio.correo, pais_id, referencia.referencia as 'pais' FROM unidad_negocio left join referencia on referencia.id = unidad_negocio.pais_id WHERE unidad_negocio.id = '".$valor."'";      $datost = $this->_main->select($query);      if ($datost[0]['pais_id'] == 195) {        $_SESSION['monedatienda'] = '$';      }else{        $_SESSION['monedatienda'] = 'Bs';      }      $data = $_SESSION['monedatienda'];      //echo $data[0][1];      echo json_encode($data);    }  }?>